export const getBadgeName = (level: StreakBadgeLevel): string => {
  const nameMap: { [key in StreakBadgeLevel]: string } = {
    PRINCIPIANTE: 'Principiante',
    ESTUDIANTE: 'Estudiante',
    DEDICADO: 'Dedicado',
    EN_LLAMAS: 'En Llamas',
    IMPARABLE: 'Imparable',
