export const getStreakEmoji = (streak: number): string => {
  if (streak >= 52) return 'üöÄ'; // 1 a√±o
  if (streak >= 24) return 'üëë'; // 6 meses
  if (streak >= 12) return '‚ö°'; // 3 meses
  if (streak >= 8) return 'üî•';  // 2 meses
  if (streak >= 4) return 'üéØ';  // 1 mes
  if (streak >= 2) return 'üìö';  // 2 semanas
  if (streak >= 1) return 'üå±';  // 1 semana
