import { PrismaClient } from '@prisma/client';
import { recordLessonCompletion } from '../src/lib/streaks';

const prisma = new PrismaClient();

async function testGoalCompletion() {
  try {
    const user = await prisma.user.findFirst();
    const course = await prisma.course.findFirst();

    if (!user || !course) {
      throw new Error('No user or course found');
    }

    console.log('üéØ Completando lecci√≥n 5 para activar la racha...');
    const result = await recordLessonCompletion(user.id, course.id, 5, 'Lecci√≥n 5 - Meta Semanal Completada');

    console.log('‚úÖ Resultado:', result);

    if (result.goalMet) {
      console.log('üéâ ¬°META SEMANAL COMPLETADA!');
      console.log('üî• Racha actual:', result.currentStreak, 'semanas');
      console.log('üí∞ Puntos ganados: 10 puntos');
    }

  } catch (error) {
    console.error('‚ùå Error:', error);
  } finally {
    await prisma.$disconnect();
  }
}

testGoalCompletion();
